    private AdView mAdView;
    Context context;
    Dialog loadingDialog;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_dash_board);
        bottomNavigationView = findViewById(R.id.botton_nav_menu);
        frameLayout = findViewById(R.id.frameLayout);
        
        FirebaseMessaging.getInstance().setAutoInitEnabled(true);

        networkConnection();

        setFragment(new HomeFragment(), HOME_FRAGMENT);

        ////////////////loading dialog
        loadingDialog = new Dialog(this);
        loadingDialog.setContentView(R.layout.dialog_banned);
        loadingDialog.setCancelable(false);
        loadingDialog.getWindow().setBackgroundDrawable(getDrawable(R.drawable.banner_slider_baground));
        loadingDialog.getWindow().setLayout(ViewGroup.LayoutParams.WRAP_CONTENT, ViewGroup.LayoutParams.WRAP_CONTENT);
        ////////////////loading dialog

        FirebaseFirestore.getInstance()
                .collection("USERS")
                .document(FirebaseAuth.getInstance().getUid())
                .get()
                .addOnCompleteListener(new OnCompleteListener<DocumentSnapshot>() {
                    @Override
                    public void onComplete(@NonNull Task<DocumentSnapshot> task) {
                        if (task.isSuccessful()){
                            String unBanned=String.valueOf(task.getResult().getString("unBanned"));

                            if (unBanned.equals("banned")){
                                loadingDialog.show();
                            }
                        }else {
                            String error=task.getException().getMessage();
                            Toast.makeText(DashBoardActivity.this, error, Toast.LENGTH_SHORT).show();
                        }
                    }
                });

        MobileAds.initialize(this, new OnInitializationCompleteListener() {
            @Override
            public void onInitializationComplete(InitializationStatus initializationStatus) {
            }
        });
        mAdView = findViewById(R.id.adView);
        AdRequest adRequest = new AdRequest.Builder().build();
        mAdView.loadAd(adRequest);

        mAdView.setAdListener(new AdListener() {
            @Override
            public void onAdLoaded() {
                // Code to be executed when an ad finishes loading.
                super.onAdLoaded();
            }

            @Override
            public void onAdFailedToLoad(LoadAdError adError) {
                // Code to be executed when an ad request fails.
                super.onAdFailedToLoad(adError);
                mAdView.loadAd(adRequest);
            }

            @Override
            public void onAdOpened() {
                // Code to be executed when an ad opens an overlay that
                // covers the screen.
                super.onAdOpened();
            }

            @Override
            public void onAdClicked() {
                // Code to be executed when the user clicks on an ad.
                super.onAdClicked();
            }

            @Override
            public void onAdClosed() {
                // Code to be executed when the user is about to return
                // to the app after tapping on an ad.
            }
        });


        bottomNavigationView.setOnNavigationItemSelectedListener(new BottomNavigationView.OnNavigationItemSelectedListener() {
            @Override
            public boolean onNavigationItemSelected(@NonNull MenuItem item) {
                switch (item.getItemId()) {
                    case R.id.home:
                        setCheckedChancel();
                        invalidateOptionsMenu();
                        setFragment(new HomeFragment(), HOME_FRAGMENT);
                        break;
                    case R.id.news:
                        setCheckedChancel();
                        invalidateOptionsMenu();
                        setFragment(new NewsFragment(), LEADERBOARD_FRAGMENT);
                        item.setChecked(true);
                        break;
                    case R.id.brows:
                        setCheckedChancel();
                        invalidateOptionsMenu();
                        setFragment(new BrowsFragment(), BROWS_FRAGMENT);
                        item.setChecked(true);
                        break;
                    case R.id.wallet:
                        setCheckedChancel();
                        invalidateOptionsMenu();
                        setFragment(new WalletFragment(), WALLET_FRAGMENT);
                        item.setChecked(true);
                        break;
                    case R.id.profile:
                        setCheckedChancel();
                        invalidateOptionsMenu();
                        setFragment(new AccountFragment(), ACCOUNT_FRAGMENT);
                        item.setChecked(true);
                        break;
                }
                return true;
            }
        });
    }

    private void setFragment(Fragment fragment, int fragmentNo) {
        if (fragmentNo != CurrentFragment) {
            CurrentFragment = fragmentNo;
            FragmentTransaction fragmentTransaction = getSupportFragmentManager().beginTransaction();
            fragmentTransaction.setCustomAnimations(R.anim.slide_from_right, R.anim.slideout_from_left);
            fragmentTransaction.replace(frameLayout.getId(), fragment);
            fragmentTransaction.commit();
        }
    }

    private void setCheckedChancel() {
        bottomNavigationView.getMenu().getItem(0).setChecked(false);
        bottomNavigationView.getMenu().getItem(1).setChecked(false);
        bottomNavigationView.getMenu().getItem(2).setChecked(false);
        bottomNavigationView.getMenu().getItem(3).setChecked(false);
        bottomNavigationView.getMenu().getItem(4).setChecked(false);
    }

